mapper.select:
SELECT *
FROM
oms_order
WHERE
delete_status = 0  AND member_id = #{memberId}

    AND order_sn = #{queryParam.orderSn}


    AND `status` = #{queryParam.status}


    AND source_type = #{queryParam.sourceType}


    AND order_type = #{queryParam.orderType}


    AND create_time LIKE concat(#{queryParam.createTime},"%")


    AND (
    receiver_name LIKE concat("%",#{queryParam.receiverKeyword},"%")
    OR receiver_phone LIKE concat("%",#{queryParam.receiverKeyword},"%")
    )

mapper.update:
UPDATE pms_sku_stock SET stock = CASE id
WHEN #{item.productSkuId} THEN stock - #{item.productQuantity}
END, lock_stock = CASE id
WHEN #{item.productSkuId} THEN lock_stock - #{item.productQuantity}
END WHERE id IN
#{item.productSkuId}

update oms_order
set status=#{status}
where id in

    #{id}

UPDATE pms_sku_stock SET lock_stock = CASE id
WHEN #{item.productSkuId} THEN lock_stock - #{item.productQuantity}
END WHERE id IN
#{item.productSkuId}
